import React, { createContext, useContext, useReducer, useRef, useState, Suspense, lazy, useEffect } from 'react';

const initialGameState = {
  isActiveGame: true,
  isWhiteTurn: true,
  isCheck: false,
  isCheckmate: false,
  moveNumber: 1,
  history: [],
  selectedPiece: '',
  selectedSquare: {
    rowIndex: -1,
    columnIndex: -1
  }
};

const initialBoardState = {
  sourceSquare: {
    rowIndex: -1,
    columnIndex: -1
  },
  targetSquare: {
    rowIndex: -1,
    columnIndex: -1
  },
  selectedPiece: '',
};

export const rowDictionary = [
  '1', '2', '3', '4', '5', '6', '7', '8'
];

export const columnDictionary = [
  'A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'
];

const GameStateContext = createContext();

// export const useGameStateContext = () => useContext(GameStateContext);

export function useGameStateContext () {
  return useContext(GameStateContext);
}

export default function GameStateProvider ({ children }) {
  const [gameState, setGameState] = useState(initialGameState);

  const setSelectedPiece = (piece) => {
    setGameState({
      ...gameState,
      selectedPiece: piece
    })
  }

  const setSelectedSquare = (rowIndex, columnIndex) => {
    setGameState({
      ...gameState,
      selectedSquare: { rowIndex, columnIndex }
    })
  }

  return (
    <GameStateContext.Provider value={{ gameState, setSelectedPiece, setSelectedSquare }}>
      {children}
    </GameStateContext.Provider>
  );
}
